import React  from 'react'
import {FiUser,FiMail } from "react-icons/fi";
import {AiOutlineCloseCircle } from "react-icons/ai";
import { ErrorMessage, Field, Form, Formik } from "formik";
import * as Yup from "yup"



//VALIDATE MESSAGES
const userJoin = Yup.object().shape({

  first_name:Yup.string()
                 .min(3, 'Too Short!')
                 .max(20, 'Too Long!')
                 .required('Required'),

  last_name:Yup.string()
               .min(3, 'Too Short!')
               .max(30, 'Too Long!')
               .required('Required'),

  email:Yup.string()
           .email('Invalid email')
           .required('Required'),
  });


const JoinUs = (props) => {
    return(
         //INTIAL STATE FOR FORM
    <Formik initialValues={{
                            first_name:'',
                            last_name:'',
                            email:''
                          }}
            validationSchema={userJoin}
            onSubmit ={ (values) => props.handleClick(values) } >


      {({ handleSubmit }) => (

          <Form className="home-page-content__form" id='5'
                onSubmit={handleSubmit}>

              <div className="home-page-content__form__group">
                 <FiUser className='icon' /> 
                 <Field type='text' name='first_name' placeholder='name'/>
                 <p><ErrorMessage name="first_name" /></p>

                </div>

              {/* <button type='submit' value='Join' className="btn-join"></button>  */}
            </Form>
      )}  
            </Formik>
    )
}


export default JoinUs




